import axios from 'axios';
const API_BASE = 'http://localhost:8081/api/reports'

export const fetchReports = (category) => {
    const url = category ? `${API_BASE}/reports?category=${category}` : `${API_BASE}/reports`;
  return axios.get(url);
}
